var fs = require( 'fs' ),
    parser = require( '../' ),
    util = require( 'util' );

fs.readFile( __dirname + '/inkscape.svg', { encoding : 'utf8' }, function( err, file ) {
    if ( err ) return console.log( err );
    var json = parser.parse( file );
    fs.writeFileSync( __dirname + '/inkscape.json', JSON.stringify( json, null, '\t' ) );
} )